<script setup>
import { ref, watch, nextTick, onMounted, onBeforeUnmount } from 'vue'
import 'vue3-tel-input/dist/vue3-tel-input.css'
import { VueTelInput } from 'vue3-tel-input'
import { useUserStore } from '~/stores/user'

const props = defineProps({ visible: Boolean })
const emit = defineEmits(['update:visible'])

const store = useUserStore()

const fullname = ref('')
const city = ref('')
const phone = ref('')
const phoneError = ref('')
const username = ref('')
const email = ref('')
const password = ref('')
const loading = ref(false)
const modal = ref(null)

function onPhoneInput(value) {
  if (typeof value === 'string') {
    const digits = value.replace(/\D/g, '')
    if (!digits.startsWith('7') || digits.length !== 11) {
      phoneError.value = 'Введите корректный российский номер (+7)'
    } else {
      phoneError.value = ''
      phone.value = digits
    }
  }
}

function close() {
  if (loading.value) return
  emit('update:visible', false)
}

function resetForm() {
  fullname.value = ''
  city.value = ''
  phone.value = ''
  phoneError.value = ''
  username.value = ''
  email.value = ''
  password.value = ''
}

async function submit() {
  if (loading.value) return
  if (!phone.value.startsWith('7') || phone.value.length !== 11) {
    phoneError.value = 'Пожалуйста, введите российский номер (+7).'
    return
  }

  loading.value = true
  try {
    await store.register({
      fullname: fullname.value,
      city: city.value,
      phone: phone.value,
      username: username.value,
      email: email.value,
      password: password.value
    })
    close()
    resetForm()
  } catch (err) {
    console.log(store.error || 'Ошибка при регистрации')
  } finally {
    loading.value = false
  }
}

// Escape / focus
function onEsc() { close() }
watch(() => props.visible, async (v) => {
  if (v) { await nextTick(); setTimeout(() => modal.value?.focus(), 50) }
})
function onKeydown(e) { if (e.key === 'Escape') onEsc() }
onMounted(() => document.addEventListener('keydown', onKeydown))
onBeforeUnmount(() => document.removeEventListener('keydown', onKeydown))
</script>





